    private void btnEncryptActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEncryptActionPerformed
        int k = Integer.valueOf(this.txtKhoa.getText());
        String s = this.txtPlainText.getText();
        int n = s.length();
        int sd, sc;
        sd = k;
        sc = n / sd + 1;
        char hr[][] = new char[sd][sc];
        int c = 0, d = 0;
        int soDu = n % sd;
        for (int i = 0; i < n; ++i){
            hr[d][c] = s.charAt(i);
            ++d;
            if (d == k){
                ++c;
                d = 0;
            }    
        }
        String result  = "";
        int sokyTu = sc;
        for (int i = 0; i < sd; ++i){
            if (i >= soDu)
                sokyTu = sc - 1;
            for (int j = 0; j < sokyTu; ++j)
                result += hr[i][j];
        }
        this.txtCipherText.setText(result);
    }//GEN-LAST:event_btnEncryptActionPerformed

    private void btnDecryptActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDecryptActionPerformed
        int k = Integer.valueOf(this.txtKhoa.getText());
        String s = this.txtCipherText.getText();
        int n = s.length();
        int sd, sc;
        sd = k;
        sc = n / sd + 1;
        char hr[][] = new char[sd][sc];
        int t = 0;
        int soDu = n % sd;
        int sokyTu = sc;
        for (int i = 0; i < sd; ++i){
            if (i >= soDu)
                sokyTu = sc - 1;
            for (int j = 0; j < sokyTu; ++j){
                hr[i][j] = s.charAt(t);
                ++t;
            }
        }
        int c = 0, d = 0;
        String result  = "";
        for (int i = 0; i < n; ++i){
            result += hr[d][c];
            ++d;
            if (d == k){
                ++c;
                d = 0;
            } 
        }
        this.txtPlainText.setText(result);
    }//GEN-LAST:event_btnDecryptActionPerformed

